// RoomSelection.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import UserRegister from '../components/UserRegistration';

const RoomSelection = () => {
  const [rooms, setRooms] = useState([]);
  var stompClient = null;
  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  }

  const onError = (err) => {
    console.log(err);

  }
  const onConnected = () => {
    stompClient.subscribe('/topic/public', onRoomsReceived);
  }



  useEffect(() => {
    // Fetch the list of available chat rooms from the server
    // For example, you can use an API call here
    // Update the setRooms state with the list of rooms
  }, []);

  const handleRoomSelected = (roomCode) => {
    // Redirect to the chat page with the selected room and username
    // For simplicity, assuming you have a default username for now
    const username = 'defaultUsername'; // Replace with the actual username
    window.location.href = `/chat/${roomCode}/${username}`;
  };

  return (
    <div className="room-selection">
      <h2>Choose or Create a Chat Room</h2>
      {/* Display the list of available chat rooms */}
      {/* Allow the user to create a new room or select an existing one */}
      {/* For simplicity, you can just display the list of rooms here */}
      {/* For example, you can use the following code: */}
      <ul>
        {rooms.map((room) => (
          <li key={room.roomCode}>
            {/* Render the room information (e.g., room name, description) */}
            {/* Allow the user to select the room */}
            <button type="button" onClick={() => handleRoomSelected(room.roomCode)}>
              Select Room
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomSelection;
