import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/registration.css';
import { useLoadingContext } from '../components/Loader';

const UserRegistration = () => {
  const [username, setUsername] = useState('');
  const { isLoading, setLoading, disableAllInputs, enableAllInputs } = useLoadingContext();

  const navigate = useNavigate();

  const handleRegister = () => {
    setLoading(true);
    disableAllInputs();
    axios
      .post('http://192.168.29.186:8080/addUser', { fullName: username })
      .then((res) => {
        // Redirect to the room selection page
        setLoading(false);
        enableAllInputs();
        navigate('/rooms', { state: { user: res.data } });
      })
      .catch((err) => {
        console.error(err);
      });
  }

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  return (
    <div className="user-registration" style={{backgroundColor: "royalblue" }}>
      {isLoading && <div className="loader">Loading...</div>}
      <div className='signup'>
      <h2>User Registration</h2>
      <input
        type="text"
        placeholder="Enter your username"
        value={username}
        onChange={handleUsernameChange}
        disabled={isLoading}
      />
      <img src="https://img.icons8.com/exit" onClick={handleRegister} />
      </div>
    </div>
  );
};

export default UserRegistration;
