// RoomSelection.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';


const RoomSelection = (props) => {
  const location = useLocation();
  const user = location.state?.user;
  const [username, setUsername] = useState(user.fullName);
  const [rooms, setRooms] = useState([]);
  const [roomDescription, setRoomDescription] = useState('');

  useEffect(() => {
    handleRoomRecieved();
  }, []);

  const handleRoomRecieved = () => {
    axios
      .get('http://localhost:8080/rooms')
      .then((res) => {
        setRooms(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleCreateRoom = () => {
    axios
      .post('http://localhost:8080/createRoom?label=' + roomDescription)
      .then((res) => {
        handleRoomRecieved();
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div className="room-selection">
      <h2>Welcome, {user.fullName}!</h2>
      <h2>Choose or Create a Chat Room</h2>
      <input
        type="text"
        placeholder="Enter room description"
        value={roomDescription}
        onChange={(e) => setRoomDescription(e.target.value)}
      />
      <button type="button" value="Create Room" onClick={handleCreateRoom} />
      <h3>Available Rooms</h3>
      <ul>
        {rooms.map((room) => (
          <li key={room.roomCode}>
            {room.label}
            <button type="button">
              Select Room
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomSelection;
