import React, { useEffect, useState, useRef } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/chatroom.css';
import { useLoadingContext } from '../components/Loader'

var stompClient = null;

const ChatPage = () => {
  const { isLoading, setLoading, disableAllInputs, enableAllInputs } = useLoadingContext();
  const location = useLocation();
  const user = location.state?.user;
  const [publicChats, setPublicChats] = useState([]);
  const [userData, setUserData] = useState({ "message": "", "connected": false });
  const [username, setUsername] = useState(user.fullName);
  const [roomCode, setRoomCode] = useState(user.roomCode);
  const navigate = useNavigate();
  const chatMessagesRef = useRef(null);
  const [label, setLabel] = useState('Room Label');

  useEffect(() => {
    if (roomCode) {
      connect();
    }

    // Set up beforeunload event listener
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [roomCode]);

  const handleBeforeUnload = () => {
    axios
      .post('http://localhost:8080/deleteUser', JSON.stringify(user))
      .catch((err) => {
        console.error(err);
      });
    leaveChatroom();
  };

  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const onConnected = () => {
    // Getting previous chats of the room
    axios
      .get('http://localhost:8080/chats?roomcode=' + roomCode)
      .then((res) => {
        setPublicChats(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
    stompClient.send(`/app/user/connectUser`, {}, JSON.stringify(user));
    stompClient.subscribe(`/user/vtalk/messages/${roomCode}`, onMessageReceived);
    axios
      .get('http://localhost:8080/roomLabel' + '?roomcode=' + roomCode)
      .then((res) => {
        setLabel(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
    setRoomCode(user.roomCode);
  };

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    console.log("recieved message:  " + payloadData);
    setPublicChats(prevChats => [...prevChats, payloadData]);

    // Scroll to the bottom of the chatMessagesRef on new message
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  };

  const onError = (err) => {
    console.log(err);
  };

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, "message": value });
  };

  const sendValue = (event) => {
    event.preventDefault();
    if (userData.message !== "") {
      setUserData({ ...userData, "connected": true });
      setLoading(true);
      disableAllInputs();
      if (stompClient) {
        var chatMessage = {
          fullName: username,
          roomCode: roomCode,
          content: userData.message,
          timestamp: new Date(),
        };
        stompClient.send(`/app/chat`, {}, JSON.stringify(chatMessage));
        setUserData({ ...userData, "message": "" });
      }
      setLoading(false);
      enableAllInputs();
    }
  };

  const leaveChatroom = () => {
    setLoading(true);
    disableAllInputs();
    if (stompClient) {
      stompClient.send(`/app/user/disconnectUser`, {}, JSON.stringify(user));
      setUserData({ ...userData, "connected": false });
      axios
        .get('http://localhost:8080/leaveRoom?roomcode=' + roomCode + '&userid=' + user.userId)
        .then((res) => {
          setLoading(false);
          enableAllInputs();
          navigate(`/rooms`, { state: { user: res.data } });
        })
        .catch((err) => {
          console.error(err);
        });
    }
  };


  return (
    <div className="chat-box">
      {isLoading && <div className="loader">Leaving The Chat Room...</div>}
      <div className='chat-top'>
        <div className="room-label">{label}</div>
        {/* <img src="https://img.icons8.com/color/exit" className='leave-button' onClick={leaveChatroom} /> */}
        <button className='leave-button' onClick={leaveChatroom}>Leave</button>
      </div>
      <div className="chat-content" ref={chatMessagesRef}>
        {publicChats.map((chat, index, chats) => {
          <div className={`message ${chat.fullName === username ? "self" : ""} ${chat.fullName ? "" : "notification"}`} key={index}>
            {chat.fullName && chat.fullName !== username && <div className="avatar">{chat.fullName[0].toUpperCase()}</div>}
            <div className="message-data">
              <div>
                {chat.fullName ? (
                  <div className='content-message'>
                    <p>{chat.timestamp}</p>
                    <p className="message-time">
                      {new Date(chat.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true })}
                    </p>
                  </div>
                ) : (
                  <p className="notification">{chat.content}</p>
                )}
              </div>
            </div>
            {chat.fullName && chat.fullName === username && <div className="avatar">{chat.fullName[0].toUpperCase()}</div>}
          </div>
        })}
      </div>

      <form onSubmit={sendValue}>
        <div className="send-message">
          <input
            type="text"
            className="input-message"
            placeholder="Enter your message..."
            value={userData.message}
            onChange={handleMessage}
            onSubmit={sendValue}
          />
          {/* <img src="https://img.icons8.com/color/sent" className='send-button' onClick={sendValue} /> */}
          <button className='send-button' onClick={sendValue}>Send</button>
        </div>
      </form>
    </div>

  );
};

export default ChatPage;
