import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLoadingContext } from '../components/Loader';
import '../css/roomselection.css';


const RoomSelection = (props) => {
  const location = useLocation();
  const user = location.state?.user;
  const [username, setUsername] = useState(user ? user.fullName : '');
  const [rooms, setRooms] = useState([]);
  const [roomDescription, setRoomDescription] = useState('');
  const navigate = useNavigate();
  const { isLoading, setLoading, disableAllInputs, enableAllInputs } = useLoadingContext();
  const [isCreating, setCreating] = useState(false);
  const [loaderMessage, setLoaderMessage] = useState('Loading...');

  useEffect(() => {
    handleRoomRecieved();

    // Set up beforeunload event listener
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handleBeforeUnload = () => {
    axios
      .get('http://192.168.29.186:8080/deleteUser' + '?id=' + user.userId)
      .catch((err) => {
        console.error(err);
      });
  };

  const handleRoomRecieved = () => {
    axios
      .get('http://192.168.29.186:8080/rooms')
      .then((res) => {
        setRooms(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleCreateRoom = () => {
    setCreating(true);
    setTimeout(() => setCreating(false), 2000);
    setLoaderMessage('Creating Room...');
    setLoading(true);
    disableAllInputs();
    axios
      .get('http://192.168.29.186:8080/createRoom?label=' + roomDescription)
      .then((res) => {
        handleRoomSelected(res.data.roomCode);
      })
      .catch((err) => {
        console.error(err);
      });
    setLoading(false);
    enableAllInputs();
  };

  const handleRoomSelected = (roomCode) => {
    setLoaderMessage('Entering Room...');
    setLoading(true);
    disableAllInputs();
    axios
      .get('http://192.168.29.186:8080/joinRoom?roomcode=' + roomCode + '&userid=' + user.userId)
      .then((res) => {
        setLoading(false);
        enableAllInputs();
        navigate(`/chat/${roomCode}/${user.fullName}`, { state: { user: res.data } });
      })
      .catch((err) => {
        handleRoomRecieved();
        alert('Room no longer exists');
        setLoading(false);
        enableAllInputs();
      });
  };

  return (
    <div className="room-selection">
      {isLoading && <div className="loader">{loaderMessage}</div>}
      <h1 style={{ textAlign: 'center'}}>Welcome, {user.fullName}!</h1>
      <div className="create-room-section">
        <h3>Create a Chat Room</h3>
        <div className="create-room-input">
          <input
            type="text"
            placeholder="Enter Room Label..."
            value={roomDescription}
            onChange={(e) => setRoomDescription(e.target.value)}
          />
            <img src="https://img.icons8.com/color/add" alt="Create Room"  onClick={handleCreateRoom}/>
        </div>
      </div>
      <div className='room-head'>
        <h3>Available Rooms</h3>
        <img src="https://img.icons8.com/color/refresh" className='refresh-button' alt="Refresh" onClick={handleRoomRecieved}/>
      </div>
      <ul className="room-cards">
        {rooms.map((room) => (
          <li key={room.roomCode} onClick={() => handleRoomSelected(room.roomCode)}>
            <div className="room-card">
              <h4>{room.label}</h4>
              {/* Add any additional room information you want to display */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomSelection;
