import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLoadingContext } from '../components/Loader';


const RoomSelection = (props) => {
  const location = useLocation();
  const user = location.state?.user;
  const [username, setUsername] = useState(user.fullName && user.fullName);
  const [rooms, setRooms] = useState([]);
  const [roomDescription, setRoomDescription] = useState('');
  const navigate = useNavigate();
  const { isLoading, setLoading, disableAllInputs, enableAllInputs } = useLoadingContext();

  useEffect(() => {
    handleRoomRecieved();

    // Set up beforeunload event listener
    window.addEventListener('beforeunload', handleBeforeUnload);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handleBeforeUnload = () => {
    axios
      .get('http://localhost:8080/deleteUser' + '?id=' + user.userId)
      .catch((err) => {
        console.error(err);
      });
  };

  const handleRoomRecieved = () => {
    axios
      .get('http://localhost:8080/rooms')
      .then((res) => {
        setRooms(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleCreateRoom = () => {
      setLoading(true);
      disableAllInputs();
      axios
        .get('http://localhost:8080/createRoom?label=' + roomDescription)
        .then((res) => {
          handleRoomRecieved();
        })
        .catch((err) => {
          console.error(err);
        });
        setLoading(false);
        enableAllInputs();
  };

  const handleRoomSelected = (roomCode) => {
    setLoading(true);
    disableAllInputs();
    axios
      .get('http://localhost:8080/joinRoom?roomcode=' + roomCode + '&userid=' + user.userId)
      .then((res) => {
        console.log(res.data)
        navigate(`/chat/${roomCode}/${user.fullName}`, { state: { user: res.data } });
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div className="room-selection">
      {isLoading && <div className="loader">Entering The Chat Room...</div>}
      <h2>Welcome, {user.fullName}!</h2>
      <h2>Choose or Create a Chat Room</h2>
      <input
        type="text"
        placeholder="Enter room description"
        value={roomDescription}
        onChange={(e) => setRoomDescription(e.target.value)}
      />
      <button type="button" onClick={handleCreateRoom}>
        Create Room
      </button>
      <h3>Available Rooms</h3>
      <ul>
        {rooms.map((room) => (
          <li key={room.roomCode}>
            {room.label}
            <button type="button" onClick={() => handleRoomSelected(room.roomCode)}>
              Select Room
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomSelection;
