import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


var stompClient =null;
const ChatPage = (props) => {
  const location = useLocation();
  const user = location.state?.user;
  const [publicChats, setPublicChats] = useState([]);
  const [userData, setUserData] = useState({ "message": "", "connected": true });
  const [username, setUsername] = useState(user.fullName);
  const [roomCode, setRoomCode] = useState(user.roomCode);
  const navigate = useNavigate();


  useEffect(() => {
    if (roomCode) {
      connect();
    }
  }, [roomCode]);

  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const onConnected = () => {
    stompClient.send(`/app/user/connectUser`, {}, JSON.stringify(user));
    stompClient.subscribe(`/app/vtalk/messages/${roomCode}`, onMessageReceived);
    setUsername(user.fullName);
    setRoomCode(user.roomCode);
  };

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    setPublicChats(prevChats => [...prevChats, payloadData]);
  };

  const onError = (err) => {
    console.log(err);
  };

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, "message": value });
  };



  const sendValue = () => {
    if (stompClient) {
      var chatMessage = {
        fullName: username,
        roomCode: roomCode, // Include roomCode in the message
        content: userData.message,
        timestamp: new Date(),
      };
      stompClient.send(`/app/message/${roomCode}`, {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, "message": "" });
    }
  };

  const leaveChatroom = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: username,
        roomCode: roomCode, // Include roomCode in the message
        status: "LEAVE"
      };
      stompClient.send(`/app/message/${roomCode}`, {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, "connected": false });
    }
  };

  return (
    <div className="chat-box">
      <div className="member-list">
        <ul>
          {/* Room members or other relevant UI can be added here */}
        </ul>
      </div>
      <div className="chat-content">
        <ul className="chat-messages">
          {publicChats.map((chat, index) => (
            <li className={`message ${chat.senderName === username && "self"}`} key={index}>
              {chat.senderName !== username && <div className="avatar">{chat.senderName}</div>}
              <div className="message-data">{chat.message}</div>
              {chat.senderName === username && <div className="avatar self">{chat.senderName}</div>}
            </li>
          ))}
        </ul>

        <div className="send-message">
          <input type="text" className="input-message" placeholder="enter the message" value={userData.message} onChange={handleMessage} />
          <button type="button" className="send-button" onClick={sendValue}>send</button>
          <button type="button" className="send-button" onClick={leaveChatroom}>leave</button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
