// RoomSelection.js
import React, { useEffect, useState } from 'react';
import SockJS from 'sockjs-client';
import { over } from 'stompjs';
import { Link } from 'react-router-dom';

let stompClient = null;
const RoomSelection = (props) => {
  const user = props.location.state.user;
  const [username, setUsername] = useState(user.fullName);
  const [roomCode, setRoomCode] = useState('');
  const [rooms, setRooms] = useState([]);
  const [roomDescription, setRoomDescription] = useState('');

  useEffect(() => {
    connect();
  }, []);

  const connect = () => {
    const Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);

    function onConnected() {
      stompClient.subscribe('app/user/topic', onRoomsReceived);
    }

    function onError(err) {
      console.log('Error:', err);
    }
  };

  const onRoomsReceived = (payload) => {
    const payloadData = JSON.parse(payload.body);
    setRooms(...rooms, payloadData);
  };

  const handleCreateRoom = () => {
    // Ensure roomDescription is not empty before creating a room
    if (roomDescription.trim() !== '') {
      if (stompClient && stompClient.connected) {
        // stompClient is connected, send the message
        stompClient.send('/app/createRoom', {}, JSON.stringify({ description: roomDescription }));
        // Assuming you have a default username for now
        const username = 'defaultUsername';
        window.location.href = `/chat/${roomCode}/${username}`;
      } else {
        // stompClient is not connected, show an error or attempt to connect
        console.error('WebSocket connection not established');
        // You can attempt to connect here or show an error to the user
        // Example: connect();
      }
    }
  };  

  const handleRoomSelected = (roomCode) => {
    // Redirect to the chat page with the selected room and username
    // For simplicity, assuming you have a default username for now
    const uname = username;
    setRoomCode(roomCode);
    window.location.href = `/chat/${roomCode}/${uname}`;
  };

  return (
    <div className="room-selection">
      <h2>Welcome, {user.fullName}!</h2>
      <h2>Choose or Create a Chat Room</h2>
      <input
        type="text"
        placeholder="Enter room description"
        value={roomDescription}
        onChange={(e) => setRoomDescription(e.target.value)}
      />
      <button type="button" onClick={handleCreateRoom}>
        Create Room
      </button>
      <h3>Available Rooms</h3>
      <ul>
        {rooms.map((room) => (
          <li key={room.roomCode}>
            <button type="button" onClick={() => handleRoomSelected(room.roomCode)}>
              Select Room
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RoomSelection;
